/**
 * Audio component
 */
$(function() {
  var $audio = $(".audio"),
      $play = $audio.find(".play"),
      $stop = $audio.find(".stop"),
      audioPlayer = new Audio();

  $play.on("click", function() {
    if ($(this).text() === 'Play') {
      audioPlayer.src = "audio/cnp-demo.mp3";
      audioPlayer.play();
      $(this).addClass("pause");
    } else {
      $(this).removeClass("pause");
      audioPlayer.pause();
    }
  });

  audioPlayer.addEventListener("playing", function() {
    $play.text("Pause");
  });

  audioPlayer.addEventListener("pause", function() {
    $play.text("Play");
  });

  audioPlayer.addEventListener("timeupdate", updateProgressBar, true);

  function updateProgressBar () {
    var elapsedTime = Math.round(audioPlayer.currentTime),
        elapsedTimeBar = null,
        canvas = document.getElementById('canvas');
        ctx = null;

    if (!!(canvas.getContext) === false) {
      return false;
    }

    ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.clientWidth, canvas.clientHeight);
    ctx.fillStyle = "rgb(0,153,220)";
    elapsedTimeBar = (elapsedTime / audioPlayer.duration) * (canvas.clientWidth);

    if (elapsedTimeBar > 0) {
      ctx.fillRect(0, 0, elapsedTimeBar, canvas.clientHeight);
    }
  }

  $stop.on("click", function() {
    audioPlayer.pause();
    $play.removeClass("pause");
    audioPlayer.src = '';
    audioPlayer.removeAttribute("src");
  });
});


